mapper.select:
SELECT * FROM (
    SELECT row_number() OVER (ORDER BY class_code DESC) AS rn, "CLASS".*
    FROM (
        SELECT * FROM "CLASS"  
        )"CLASS" WHERE class_type = #{classType}  
    )
WHERE rn BETWEEN (#{currentPage}-1)*#{messagePerPage}+1 AND #{currentPage}*#{messagePerPage}

		SELECT COUNT(*) FROM (
	        SELECT * FROM "CLASS"  
        ) WHERE class_type = #{classType}

		SELECT * FROM (
		    SELECT row_number() OVER (ORDER BY class_code DESC) AS rn, "CLASS".*
		    FROM (
		        SELECT * FROM (
					SELECT * FROM "CLASS"
					WHERE class_code  IN(SELECT DISTINCT class_code FROM "ORDER" WHERE order_process <= 1)
                    )  
		        )"CLASS" WHERE class_type = #{classType}  
		    )
		WHERE rn BETWEEN (#{currentPage}-1)*#{messagePerPage}+1 AND #{currentPage}*#{messagePerPage}

		SELECT COUNT(*) FROM (
	        SELECT * FROM (
				SELECT * FROM "CLASS"
				WHERE class_code  IN(SELECT DISTINCT class_code FROM "ORDER" WHERE order_process <= 1)
				)  
        ) WHERE class_type = #{classType}

mapper.update:
UPDATE "CLASS"
SET class_name = #{className}, class_content = #{classContent},
class_category = #{classCategory}, run_time = #{runTime}, 
price = #{price}, class_url = , thumbnail_img = #{thumbnailImg}, 
content_img = #{contentImg}, max_student = #{maxStudent} 
WHERE class_code = #{classCode}

mapper.select:
SELECT CONCAT(thumbnail_img, content_img)
FROM "CLASS"
WHERE class_code = #{classCode}

SELECT ROW_NUMBER() OVER(ORDER BY payment_date) AS stu_no, user_id, nickname, gender, payment_date, order_process
FROM "ORDER" JOIN "USER" USING(user_no)
WHERE class_code = #{classCode}
